mapper.select:
/* pharmai.formulation.selectPageListProject */ SELECT PRJCT_ID , PRJCT_NM , PRJCT_TYPE , STEP_CD , USE_YN , UPDUSR_NO , UPDT_DT FROM TB_PRJ_MST WHERE 1=1 AND USE_YN = 'Y' AND PRJCT_TYPE = 'F'
AND PRJCT_NM LIKE CONCAT('%', #{sch_text, jdbcType=VARCHAR}, '%')
AND DATE_FORMAT(REGIST_DT,'%Y%m%d') >= #{sch_disp_begin_de_tmp, jdbcType=VARCHAR}
AND DATE_FORMAT(REGIST_DT,'%Y%m%d') <= #{sch_disp_end_de_tmp, jdbcType=VARCHAR}
ORDER BY REGIST_DT DESC LIMIT #{limitStart, jdbcType=VARCHAR}, #{limitCount, jdbcType=VARCHAR}

/* pharmai.formulation.bioSelectPageListProject */ SELECT PRJCT_ID , PRJCT_NM , PRJCT_TYPE , STEP_CD , USE_YN , UPDUSR_NO , UPDT_DT FROM TB_PRJ_MST WHERE 1=1 AND USE_YN = 'Y' AND PRJCT_TYPE = 'BF'
AND PRJCT_NM LIKE CONCAT('%', #{sch_text, jdbcType=VARCHAR}, '%')
AND DATE_FORMAT(REGIST_DT,'%Y%m%d') >= #{sch_disp_begin_de_tmp, jdbcType=VARCHAR}
AND DATE_FORMAT(REGIST_DT,'%Y%m%d') <= #{sch_disp_end_de_tmp, jdbcType=VARCHAR}
ORDER BY REGIST_DT DESC LIMIT #{limitStart, jdbcType=VARCHAR}, #{limitCount, jdbcType=VARCHAR}

/* pharmai.formulation.selectTotCntProject */
SELECT
	COUNT(1) AS TOT_CNT
FROM TB_PRJ_MST
WHERE 1=1
AND USE_YN = 'Y'
AND PRJCT_TYPE = 'F'

	AND PRJCT_NM LIKE CONCAT('%', #{sch_text, jdbcType=VARCHAR}, '%')



	AND DATE_FORMAT(REGIST_DT,'%Y%m%d') >= #{sch_disp_begin_de_tmp, jdbcType=VARCHAR}




	AND DATE_FORMAT(REGIST_DT,'%Y%m%d') <= #{sch_disp_end_de_tmp, jdbcType=VARCHAR}

/* pharmai.formulation.bioSelectTotCntProject */
SELECT
	COUNT(1) AS TOT_CNT
FROM TB_PRJ_MST
WHERE 1=1
AND USE_YN = 'Y'
AND PRJCT_TYPE = 'BF'

	AND PRJCT_NM LIKE CONCAT('%', #{sch_text, jdbcType=VARCHAR}, '%')



	AND DATE_FORMAT(REGIST_DT,'%Y%m%d') >= #{sch_disp_begin_de_tmp, jdbcType=VARCHAR}




	AND DATE_FORMAT(REGIST_DT,'%Y%m%d') <= #{sch_disp_end_de_tmp, jdbcType=VARCHAR}

